function getProfessors(){fetch(uri).then(n=>n.json()).then(n=>_displayProfessors(n)).catch(n=>console.error("Unable to get the Professors",n))}function _displayCount(n){const t=n===1?"Professor":"Professors";document.getElementById("counter").innerText=`${n} ${t}`}function _displayProfessors(n){const t=document.getElementById("professors");t.innerHTML="";_displayCount(n.length);n.forEach(n=>{let i=t.insertRow(),r=i.insertCell(0),u=document.createTextNode(n.name);r.appendChild(u);let f=i.insertCell(1),e=document.createTextNode(n.surname);f.appendChild(e);let o=i.insertCell(2),s=document.createTextNode(n.email);o.appendChild(s)});professors=n}const uri=window.location.origin+"/api/professors/";let professors=[];